INSERT INTO `wp_postmeta` VALUES ('3919', '2594', '_tx_testimonial_meta', 'a:3:{s:28:\"_tx_testimonial_details_name\";s:16:\"Ahmad Sirajuddin\";s:32:\"_tx_testimonial_details_position\";s:28:\"Ketum LDJ KISI ITS 2015/2016\";s:35:\"_tx_testimonial_details_testimonial\";s:843:\"Alhamdulillah,  saya sangat bersyukur kepada Allah SWT,  Saya merasa sangat beruntung  bisa merasakan penddidikan di Amanatul Ummah, di sini sistem pendikan dan pembinaan sangat dirasakan manfaatnya dan  masih membekas hingga sekarang.\r\nsetiap santri terus digembleng oleh Pak Kyai dan para guru untuk berani bermimpi besar  disertai dengan aksi nyata. \r\n\r\nImpian-impian yang impossible akan menjadi possible jika diiringi ikhtiar, iman, dan tawakal begitulah salah satu pelajaran bermakna yang saya dapatkan selama belajar di Amanatul Ummah, \r\n\r\nSemoga Allah mebalas kerja keras semua elemen yang terlibat dalam proses belajar di Amanatul ummah hingga berujung pada pencapaian Visi yang nyata yakni menccetak 10 ribu doktor lulusan dari Amantul Ummah demi iizil islam dan muslimin dan demi terwujudnya indoensia yang adil dan makmur. Amiin..!\";}');
